		current.setAbortAction(true);
    }

	var isToday = niutils.isSelectedDateToday(current.u_when_needed);

	// If isToday methiod returns true, the selected date is today
	if(isToday == true) {
		gs.addErrorMessage("You cannot submit Needit request for today.");
