sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }

   //Type appropriate comment here, and begin script below
	var emailGA = new GlideAjax('GetEmailAddress');
	emailGA.addParam('sysparm_name', 'getEmail');
	emailGA.addParam('sysparm_userID', this.getParameter('u_requested_for'));
	emailGA.getXMLAnswer(function(response) {
		emailString = response;
		if(emailString) {
			g_form.setValue('u_requested_for_email', emailString);
		}
	});
}
